import { useState,useEffect } from "react"
import { createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged, signOut} from "firebase/auth"
import {auth} from './firebase-config';
import { Button, FormControl, InputLabel, TextField} from "@mui/material";
export default function SignUp(){
    const [user, setUser] = useState({});
    const [pageShown,setPageShown] = useState(true);
    const [count,setCount] = useState(0);

    function growCount(){
      setCount(count+1);
    }
    useEffect(()=>{
      console.log('triggered')
    })
    /*
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });*/
    const changeState = () => {
      setPageShown(!pageShown)
    }
    function loginPage(){
      return (
        <div>
          {/*
          <FormControl style={{backgroundColor:'white',width:'30vw'}}>
            <TextField id="email-login"
              label="Email..."
              style={{width:'80%',marginLeft:'auto',marginRight:'auto',marginTop:'5vh'}}
            />
            <TextField id="password-login"
            label="Password..."
            style={{width:'80%',marginLeft:'auto',marginRight:'auto',marginTop:'5vh'}}
            />
            <Button onClick={login} style={{marginTop:'5vh',width:'80%',marginLeft:'auto',marginRight:'auto'}} variant="contained">Log In</Button>
            <Button onClick={logout} style={{marginTop:'5vh',width:'80%',marginLeft:'auto',marginRight:'auto'}} variant="contained">Sign Out</Button>
            <h4> User Logged In: </h4>
            {user?.email}
            <Button onClick={setPageShown('register')}>Already have an account?</Button>
      </FormControl>*/}
        <h1>login page</h1>
        <button onClick={changeState}>switch</button>
       </div>
      )
    }

    function signUpPage(){
      return (
        <div>
          <h1>sign up page</h1>
          <button onClick={setPageShown(true)}></button>
          {/*
        <div>
          <FormControl style={{backgroundColor:'white',width:'30vw'}}>
            <h1 style={{color:'black',fontfamily:'monospace'}}>Sign Up</h1>
            <TextField id="email-register"
              label="Email..."
              style={{width:'80%',marginLeft:'auto',marginRight:'auto',marginTop:'5vh'}}
            />
            <TextField id="password-register"
              label="Password..."
              style={{width:'80%',marginLeft:'auto',marginRight:'auto',marginTop:'5vh'}}
              />
            <Button onClick={register} style={{marginTop:'5vh',width:'80%',marginLeft:'auto',marginRight:'auto'}} variant="contained"> Create User</Button>
          </FormControl>
      </div>*/}
        </div>
      )
    }


    const register = async() => {
        try {
            const user = await createUserWithEmailAndPassword(auth, document.getElementById('email-register').value, document.getElementById('password-register').value)
            console.log(user)
        }
        catch(error) {
            console.log(error.message)
        }
    }
    
    const login = async() => {
        try {
            const user = await signInWithEmailAndPassword(
              auth,
              document.getElementById('email-login').value,
              document.getElementById('password-login').value
            );
            console.log(user);
          } catch (error) {
            console.log(error.message);
          }
    }
  
    const logout = async() => {
        await signOut(auth)
    }
    return (
      <button onClick={growCount}>{count}</button>
    )
}
