import { useState } from "react"
import { createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged, signOut} from "firebase/auth"
import {auth} from './firebase-config';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import { Button, FormControl, InputLabel, TextField} from "@mui/material";
export default function SignUp(){
    const [registerEmail,setRegisterEmail] = useState('')
    const [registerPassword,setRegisterPassword] = useState('')
    const [loginEmail, setLoginEmail] = useState('')
    const [loginPassword,setLoginPassword] = useState('');
    const [user, setUser] = useState({});
    
    onAuthStateChanged(auth, (currentUser) => {
        setUser(currentUser);
      });


    const register = async() => {
        try {
            const user = await createUserWithEmailAndPassword(auth, registerEmail, registerPassword)
            console.log(user)
        }
        catch(error) {
            console.log(error.message)
        }
    }
    
    const login = async() => {
        try {
            const user = await signInWithEmailAndPassword(
              auth,
              loginEmail,
              loginPassword
            );
            console.log(user);
          } catch (error) {
            console.log(error.message);
          }
    }
  
    const logout = async() => {
        await signOut(auth)
    }
    return (
      <div>
        <h1 style={{color:'white',fontfamily:'monospace'}}>Login</h1>
        <FormControl sx = {{width:'30vw',marginLeft:'auto',marginRight:'auto',height:'70vh',backgroundColor:'white'}} variant="filled">
        <TextField
          label="Username..."
          onChange={(event) => {
            setRegisterEmail(event.target.value);
          }}
          style={{width:'80%',marginLeft:'auto',marginRight:'auto',marginTop:'5vh'}}
        />
        <TextField
          label="Password..."
          onChange={(event) => {
            setRegisterPassword(event.target.value);
          }}
          style={{width:'80%',marginLeft:'auto',marginRight:'auto',marginTop:'5vh'}}
           />

        <Button onClick={register}> Create User</Button>

        <h3> Login </h3>
        <TextField
          label="Email..."
          onChange={(event) => {
            setLoginEmail(event.target.value);
          }}
        />
        <TextField
          label="Password..."
          onChange={(event) => {
            setLoginPassword(event.target.value);
          }}
        />

        <Button onClick={login}> Login</Button>
        <h4> User Logged In: </h4>
        {user?.email}

        </FormControl>
      </div>
    )
}
